{
    "type": "OKTA_SIGN_ON",
    "name": "Okta Corp Policy",
    "description": "Standard policy for every employee",
    "conditions": {
        "people": {
            "groups": {
                "include": [
                    "{{groupId}}"
                ]
            }
        }
    }
}

{
    "type": "SIGN_ON",
    "status": "ACTIVE",
    "name": "Skip Factor Challenge when On-Prem",
    "conditions": {
      "network": {
        "connection": "ZONE",
            "include": [
            "{{networkZoneId}}"
        ]
    },
      "authContext": {
        "authType": "ANY"
      }
    },
    "actions": {
      "signon": {
        "access": "ALLOW",
        "requireFactor": false,
        "rememberDeviceByDefault": false,
        "session": {
          "usePersistentCookie": false,
          "maxSessionIdleMinutes": 720,
          "maxSessionLifetimeMinutes": 0
        }
      }      
    }
}

{
    "type": "PASSWORD",
    "name": "Password Policy",
    "description": "The test policy applies to users with Okta managed passwords.",
    "conditions": {
        "people": {
            "groups": {
                "include": [
                    "{{groupId}}"
                ]
            }
        },
        "authProvider": {
            "provider": "OKTA"
        }
    },
    "settings": {
        "password": {
            "complexity": {
                "minLength": 8,
                "minLowerCase": 1,
                "minUpperCase": 1,
                "minNumber": 1,
                "minSymbol": 0,
                "excludeUsername": true
            },
            "age": {
                "maxAgeDays": 90,
                "expireWarnDays": 5,
                "minAgeMinutes": 0,
                "historyCount": 10
            },
            "lockout": {
                "maxAttempts": 10,
                "autoUnlockMinutes": 60,
                "showLockoutFailures": true
            }
        },
        "recovery": {
       "factors": {
         "recovery_question": {
           "status": "ACTIVE",
           "properties": {
             "complexity": {
               "minLength": 4
             }
           }
         },
         "okta_email": {
           "status": "ACTIVE",
           "properties": {
             "recoveryToken": {
               "tokenLifetimeMinutes": 60
             }
           }
         },
         "okta_sms": {
           "status": "INACTIVE"
         },
         "okta_call": {
           "status": "INACTIVE"
         }
       }
     },
     "delegation": {
       "options": {
         "skipUnlock": false
       }
     }
   }
  }

{
