    "settings": {
        "password": {
            "complexity": {
                "minLength": 8,
                "minLowerCase": 1,
                "minUpperCase": 1,
                "minNumber": 1,
                "minSymbol": 0,
                "excludeUsername": true
            },
            "age": {
                "maxAgeDays": 90,
                "expireWarnDays": 5,
                "minAgeMinutes": 0,
                "historyCount": 10
            },
            "lockout": {
                "maxAttempts": 10,
                "autoUnlockMinutes": 60,
                "showLockoutFailures": true
            }
        },
        "recovery": {
            "credentials": {
                "recoveryToken": {
                    "tokenLifetimeMinutes": 60
                },
                "securityAnswer": {
                    "complexity": {
                        "minLength": 4
                    }
                },
                "sms": {
                    "selfService": true
                }
            }
        }
    }
}

